import React from "react";
import AddUser from "./AddUser";

class Splitter extends React.Component {
  constructor() {
    super();
    this.state = {
      numberOfUsers: 0,
      participants: [
        {
          id: 0,
          name: "",
          contribution: 0,
          amountPayable: 0
        }
      ],

      product: [
        {
          name: "",
          price: 0,
          participants: []
        }
      ],

      user: {}
    };
  }

  AddUser() {
    let tempUser = {
      name: document.getElementById("userName")
    };

    console.log(tempUser);
  }

  render() {
    <div>
      <input type="input" placeholder="name" id="userName" />
      <br />
      <input type="button" onClick={this.AddUser} />
    </div>;
  }
}

export default Splitter;
