import React from "react";

class Splitter extends React.Component {
  constructor() {
    super();
    this.state = {
      numberOfUsers: 0,
      participants: [
        {
          id: 0,
          name: "",
          contribution: 0,
          amountPayable: 0
        }
      ],

      product: [
        {
          name: "",
          price: 0,
          participants: [],
          user: 0
        }
      ],

      user: {}
    };

    this.AddUser = this.AddUser.bind(this);
  }

  AddUser() {
    this.setState(prevState => {
      let tempUser = {
        id: prevState.numberOfUsers + 1,
        name: document.getElementById("userName").value,
        contribution: 0,
        amountPayable: 0
      };

      let newParticipans = [];

      if (tempUser.id === 1) newParticipans.push(tempUser);
      else {
        newParticipans = prevState.participants;
        newParticipans.push(tempUser);
      }
      return {
        participants: newParticipans,
        numberOfUsers: tempUser.id
      };
    });
    document.getElementById("userName").value = "";
  }

  render() {
    debugger;
    let userList =
      this.state.participants.length > 0 &&
      this.state.participants.map((user, i) => {
        return (
          <option key={i} value={user.id}>
            {user.id}
          </option>
        );
      });
    return (
      <div>
        <div>
          <input type="input" placeholder="name" id="userName" />
          <br />
          <input type="button" onClick={this.AddUser} value="Add User" />
        </div>
        <br />
        <br />
        <div>
          <input type="input" placeholder="Expense" id="product" />
          <label>
            <select id="user">{userList}</select> Who Paid?
          </label>
        </div>
      </div>
    );
  }
}

export default Splitter;
