import React from "react";

class Splitter extends React.Component {
  constructor() {
    super();
    this.state = {
      numberOfUsers: 0,
      participants: [
        {
          id: 0,
          name: "",
          contribution: 0,
          amountPayable: 0
        }
      ],

      product: [
        {
          name: "",
          price: 0,
          participants: []
        }
      ],

      user: {}
    };

    this.AddUser = this.AddUser.bind(this);
  }

  AddUser() {
    this.setState(prevState => {
      console.log(this.state.participants);
      debugger;
      let tempUser = {
        id: prevState.numberOfUsers + 1,
        name: document.getElementById("userName").value,
        contribution: 0,
        amountPayable: 0
      };

      let newParticipans;

      if (tempUser.id === 1) newParticipans = tempUser;
      else newParticipans = prevState.participants.push(tempUser);

      return {
        participants: newParticipans
      };
    });

    console.log(this.state.participants);
  }

  render() {
    return (
      <div>
        <input type="input" placeholder="name" id="userName" />
        <br />
        <input type="button" onClick={this.AddUser} value="Add User" />
      </div>
    );
  }
}

export default Splitter;
