import React from "react";

class Splitter extends React.Component {
  constructor() {
    super();
    this.state = {
      numberOfUsers: 0,
      participants: [
        {
          id: 0,
          name: "",
          contribution: 0,
          amountPayable: 0
        }
      ],

      products: [],

      user: {},

      product: {
        name: "",
        price: 0,
        user: 0,
        participants: []
      }
    };

    this.AddUser = this.AddUser.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.AddExpense = this.AddExpense.bind(this);
    this.resetProduct = this.resetProduct.bind(this);
  }

  AddUser() {
    this.setState(prevState => {
      let tempUser = {
        id: prevState.numberOfUsers + 1,
        name: document.getElementById("userName").value,
        contribution: 0,
        amountPayable: 0
      };
      document.getElementById("userName").value = "";
      let newParticipans = [];

      if (tempUser.id === 1) newParticipans.push(tempUser);
      else {
        newParticipans = prevState.participants;
        newParticipans.push(tempUser);
      }

      let productUsers = prevState.product.participants;
      productUsers.push({
        id: tempUser.id,
        name: tempUser.name,
        selected: false
      });
      return {
        participants: newParticipans,
        numberOfUsers: tempUser.id,
        product: { ...prevState.product, participants: productUsers }
      };
    });
  }

  handleChange(event) {
    const { name, value, type, checked } = event.target;
    if (type === "checkbox") {
      this.setState(prevState => {
        const users =
          prevState.product.participants &&
          prevState.product.participants.length > 0
            ? prevState.product.participants
            : [];
        const userIndex = users.findIndex(user => user.id === Number(value));

        users[userIndex].selected = checked;

        return {
          product: { ...prevState.product, participants: users }
        };
      });
    } else {
      this.setState(prevState => {
        return {
          product: { ...prevState.product, [name]: value }
        };
      });
    }
  }

  AddExpense() {
    this.setState(prevState => {
      let newProducts = prevState.products;
      newProducts.push(prevState.product);
      return {
        products: newProducts
      };
    });
  }

  resetProduct() {
    this.setState(prevState => {
      const oldParticipants = prevState.product.participants;
      oldParticipants.forEach(element => {
        element.selected = false;
      });
      return {
        product: { name: "", price: 0, user: 0, participants: oldParticipants }
      };
    });
  }
  render() {
    console.log(this.state.products);
    let userListForDropdown =
      this.state.numberOfUsers > 0 &&
      this.state.participants.map((user, i) => {
        return (
          <option key={i} value={user.id}>
            {user.name}
          </option>
        );
      });

    let userListForCheckBox =
      this.state.numberOfUsers > 0 &&
      this.state.product.participants.map((user, i) => {
        return (
          <label key={i}>
            <input
              type="checkbox"
              value={user.id}
              onChange={this.handleChange}
              checked={user.selected}
              name={user.name}
            />
            {user.name}
          </label>
        );
      });

    let userListForTable =
      this.state.numberOfUsers > 0 &&
      this.state.participants.map((user, i) => {
        return (
          <tr key={i}>
            <td>{user.name}</td>
            <td>Rs {user.contribution}</td>
          </tr>
        );
      });

    return (
      <div>
        <div className="AddUser">
          <input type="input" placeholder="name" id="userName" />
          <br />
          <input type="button" onClick={this.AddUser} value="Add User" />
        </div>
        <br />

        <table className="UserTable">
          <thead>
            <tr>
              <th>Participants</th>
              <th>Total Spent</th>
            </tr>
          </thead>
          <tbody>{userListForTable}</tbody>
        </table>
        <br />

        <div className="AddExpense">
          <input
            type="input"
            placeholder="On What?"
            id="product"
            name="name"
            value={this.state.product.name}
            onChange={this.handleChange}
          />
          <br />

          <input
            type="number"
            placeholder="Amount"
            id="price"
            name="price"
            value={this.state.product.price}
            onChange={this.handleChange}
          />
          <br />

          <label>
            Who Paid?
            <select
              id="user"
              name="user"
              value={this.state.product.user}
              onChange={this.handleChange}
            >
              <option value={0}>Select User</option>
              {userListForDropdown}
            </select>
          </label>
          <br />

          {userListForCheckBox}
          <br />

          <input type="button" onClick={this.AddExpense} value="Add Expense" />
        </div>
      </div>
    );
  }
}

export default Splitter;
