import React from "react";

function UserTable(props) {
  let userListForTable = props.participants.map((user, i) => {
    return (
      <tr key={i}>
        <td>{user.name}</td>
        <td>Rs {user.contribution}</td>
      </tr>
    );
  });

  return (
    props.numberOfUsers > 0 && (
      <div>
        <h3>Participants</h3>
        <table align="center">
          <thead>
            <tr>
              <th>Participants</th>
              <th>Total Spent</th>
            </tr>
          </thead>
          <tbody>{userListForTable}</tbody>
        </table>
      </div>
    )
  );
}

export default UserTable;
