import React from "react";

import AddUser from "./AddUser.js";
import UserTable from "./UserTable.js";
import AddExpense from "./AddExpense.js";

class Splitter extends React.Component {
  constructor() {
    super();
    this.state = {
      numberOfUsers: 0,
      participants: [
        {
          id: 0,
          name: "",
          contribution: 0,
          amountPayable: 0
        }
      ],
      products: []
    };

    this.saveUser = this.saveUser.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.AddExpense = this.AddExpense.bind(this);
    this.resetProduct = this.resetProduct.bind(this);
  }

  saveUser(user) {
    let tempUser = {
      id: user.id,
      name: user.name,
      contribution: 0,
      amountPayable: 0
    };

    let newParticipans = user.id > 1 ? this.state.participants : [];
    newParticipans.push(tempUser);

    let productUsers = this.state.product.participants;
    productUsers.push({
      id: user.id,
      name: user.name,
      selected: false
    });

    this.setState(prevState => {
      return {
        participants: newParticipans,
        numberOfUsers: user.id,
        product: { ...prevState.product, participants: productUsers }
      };
    });
  }

  handleChange(event) {
    const { name, value, type, checked } = event.target;
    if (type === "checkbox") {
      const users =
        this.state.product.participants &&
        this.state.product.participants.length > 0
          ? this.state.product.participants
          : [];
      const userIndex = users.findIndex(user => user.id === Number(value));

      users[userIndex].selected = checked;

      this.setState(prevState => {
        return {
          product: { ...prevState.product, participants: users }
        };
      });
    } else {
      this.setState(prevState => {
        return {
          product: { ...prevState.product, [name]: value }
        };
      });
    }
  }

  AddExpense() {
    this.setState(prevState => {
      let newProducts = prevState.products;
      newProducts.push(prevState.product);
      return {
        products: newProducts
      };
    });
  }

  resetProduct() {
    this.setState(prevState => {
      const oldParticipants = prevState.product.participants;
      oldParticipants.forEach(element => {
        element.selected = false;
      });
      return {
        product: { name: "", price: 0, user: 0, participants: oldParticipants }
      };
    });
  }
  render() {
    return (
      <div>
        <AddUser
          saveUser={this.saveUser}
          currentId={this.state.numberOfUsers}
        />
        <br />

        <UserTable
          participants={this.state.participants}
          numberOfUsers={this.state.numberOfUsers}
        />
        <br />

        <AddExpense
          participants={this.state.participants}
          numberOfUsers={this.state.numberOfUsers}
        />
      </div>
    );
  }
}

export default Splitter;
