import React from "react";

class Splitter extends React.Component {
  constructor() {
    super();
    this.state = {
      numberOfUsers: 0,
      participants: [
        {
          id: 0,
          name: "",
          contribution: 0,
          amountPayable: 0
        }
      ],

      product: [
        {
          name: "",
          price: 0,
          participants: [],
          user: 0
        }
      ],

      user: {}
    };

    this.AddUser = this.AddUser.bind(this);
  }

  AddUser() {
    this.setState(prevState => {
      let tempUser = {
        id: prevState.numberOfUsers + 1,
        name: document.getElementById("userName").value,
        contribution: 0,
        amountPayable: 0
      };
      document.getElementById("userName").value = "";
      let newParticipans = [];

      if (tempUser.id === 1) newParticipans.push(tempUser);
      else {
        newParticipans = prevState.participants;
        newParticipans.push(tempUser);
      }
      return {
        participants: newParticipans,
        numberOfUsers: tempUser.id
      };
    });
  }

  handleChecked(event) {}

  render() {
    debugger;
    console.log(this.state.participants);
    let userList =
      this.state.numberOfUsers > 0 &&
      this.state.participants.map((user, i) => {
        return (
          <option key={i} value={user.id}>
            {user.name}
          </option>
        );
      });

    let userListForCheckBox =
      this.state.numberOfUsers > 0 &&
      this.state.participants.map((user, i) => {
        return (
          <label>
            <input
              type="checkbox"
              value={user.id}
              onChange={this.handleChecked}
            />
            {user.name}
          </label>
        );
      });

    let userListForTable =
      this.state.numberOfUsers > 0 &&
      this.state.participants.map((user, i) => {
        return (
          <tr>
            <td>{user.name}</td>
            <td>Rs {user.contribution}</td>
          </tr>
        );
      });

    return (
      <div>
        <div>
          <input type="input" placeholder="name" id="userName" />
          <br />
          <input type="button" onClick={this.AddUser} value="Add User" />
        </div>
        <br />
        <table style={{textAlign:'center'}}>
          <th>
            <td>Participants</td>
            <td>Total Spent</td>
          </th>
          {userListForTable}
        </table>
        <br />
        <div>
          <input type="input" placeholder="On What?" id="product" />
          <br />
          <input type="input" placeholder="Amount" id="price" />
          <br />
          <label>
            Who Paid?
            <select id="user">{userList}</select>
          </label>
          <br />
          {userListForCheckBox}
        </div>
      </div>
    );
  }
}

export default Splitter;
