import React from "react";

class AddExpense extends React.Component {
  constructor() {
    super();
    this.state = {};
  }

  render() {
    return (
      <div>
        <input
          type="input"
          placeholder="On What?"
          id="product"
          name="name"
          value={this.state.product.name}
          onChange={this.handleChange}
        />
        <br />

        <input
          type="number"
          placeholder="Amount"
          id="price"
          name="price"
          value={this.state.product.price}
          onChange={this.handleChange}
        />
        <br />

        <label>
          Who Paid?
          <select
            id="user"
            name="user"
            value={this.state.product.user}
            onChange={this.handleChange}
          >
            <option value={0}>Select User</option>
            {userListForDropdown}
          </select>
        </label>
        <br />

        {userListForCheckBox}
        <br />

        <input type="button" onClick={this.AddExpense} value="Add Expense" />
      </div>
    );
  }
}

export default AddExpense;
