import React from "react";

class Splitter extends React.Component {
  constructor() {
    super();
    this.state = {
      numberOfUsers: 0,
      participants: [
        {
          id: 0,
          name: "",
          contribution: 0,
          amountPayable: 0
        }
      ],

      products: [
        {
          name: "",
          price: 0,
          participants: [],
          user: 0
        }
      ],

      user: {},

      product: {
        name: "",
        price: 0,
        participants: [],
        user: 0
      }
    };

    this.AddUser = this.AddUser.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  AddUser() {
    this.setState(prevState => {
      let tempUser = {
        id: prevState.numberOfUsers + 1,
        name: document.getElementById("userName").value,
        contribution: 0,
        amountPayable: 0
      };
      document.getElementById("userName").value = "";
      let newParticipans = [];

      if (tempUser.id === 1) newParticipans.push(tempUser);
      else {
        newParticipans = prevState.participants;
        newParticipans.push(tempUser);
      }
      return {
        participants: newParticipans,
        numberOfUsers: tempUser.id
      };
    });
  }

  handleChange(event) {
    const { name, value } = event.target;
    this.setState({
      product: { [name]: value }
    });
  }

  AddExpense() {}
  render() {
    console.log(this.state.product);
    let userListForDropdown =
      this.state.numberOfUsers > 0 &&
      this.state.participants.map((user, i) => {
        return (
          <option key={i} value={user.id}>
            {user.name}
          </option>
        );
      });

    let userListForCheckBox =
      this.state.numberOfUsers > 0 &&
      this.state.participants.map((user, i) => {
        return (
          <label>
            <input
              type="checkbox"
              value={user.id}
              onChange={this.handleChecked}
            />
            {user.name}
          </label>
        );
      });

    let userListForTable =
      this.state.numberOfUsers > 0 &&
      this.state.participants.map((user, i) => {
        return (
          <tr>
            <td>{user.name}</td>
            <td>Rs {user.contribution}</td>
          </tr>
        );
      });

    return (
      <div>
        <div className="AddUser">
          <input type="input" placeholder="name" id="userName" />
          <br />
          <input type="button" onClick={this.AddUser} value="Add User" />
        </div>
        <br />

        <table className="UserTable">
          <thead>
            <tr>
              <th>Participants</th>
              <th>Total Spent</th>
            </tr>
          </thead>
          {userListForTable}
        </table>
        <br />

        <div className="AddExpense">
          <input
            type="input"
            placeholder="On What?"
            id="product"
            name="name"
            value={this.state.product.name}
            onChange={this.handleChange}
          />
          <br />

          <input
            type="money"
            placeholder="Amount"
            id="price"
            name="price"
            value={this.state.product.price}
            onChange={this.handleChange}
          />
          <br />

          <label>
            Who Paid?
            <select
              id="user"
              name="user"
              value={this.state.product.user}
              onChange={this.handleChange}
            >
              {userListForDropdown}
            </select>
          </label>
          <br />

          {userListForCheckBox}
          <br />

          <input type="button" onClick={this.AddExpense} value="Add Expense" />
        </div>
      </div>
    );
  }
}

export default Splitter;
